#include <bits/stdc++.h>
using namespace std;

class ForgetfulAddition {
public:
	int to_int(const string& s) {
		stringstream sin(s);
		int n; sin >> n;
		return n;
	}

	int minNumber(string expression) {
		int result = numeric_limits<int>::max();
		for (int i = 1; i < expression.length(); ++i) {
			const string a = expression.substr(0, i);
			const string b = expression.substr(i);
			result = min(result, to_int(a) + to_int(b));
		}
		return result;
	}
};

